mapper.select:
select count(*)
from T_PG_BANK_PAYMENT_ORDER
where BANK_ORDER_NO = #{bankOrderNo,jdbcType=VARCHAR}
      and TRANS_STATUS = #{transStatus,jdbcType=INTEGER}

select MER_TYPE
from T_PG_BANK_PAYMENT_ORDER
where BANK_ORDER_NO = #{bankOrderNo,jdbcType=VARCHAR}

mapper.update:
update T_PG_BANK_PAYMENT_ORDER
BANK_ORDER_SEQ_ID = #{bankOrderSeqId,jdbcType=VARCHAR},
TRANS_STATUS = #{transStatus,jdbcType=INTEGER},
BANK_ORDER_STATUS = #{bankOrderStatus,jdbcType=CHAR}, FAIL_REASON = #{failReason,jdbcType=VARCHAR}, UPDATED_DATE = SYSDATE()
where BANK_ORDER_NO = #{bankOrderNo,jdbcType=VARCHAR}
and TRANS_STATUS != #{transStatus,jdbcType=INTEGER}
